import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Observable } from 'rxjs';
import { switchMap } from 'rxjs/operators';
import { Character } from 'src/app/shared/components/interfaces/character.interface';
import { CharacterService } from 'src/app/shared/services/character.service';
import { Location } from '@angular/common';

@Component({
  selector: 'app-character-details',
  templateUrl: './character-details.component.html',
  styleUrls: ['./character-details.component.css']
})
export class CharacterDetailsComponent implements OnInit {
  character!: Observable<Character>;

  constructor(
    private route: ActivatedRoute,
    private characterSvc: CharacterService,
    private location: Location
  ) {}

  ngOnInit(): void {
    this.character = this.route.params.pipe(
      switchMap((params) => {
        const id = params['id'];
        console.log(id);
        return this.characterSvc.getDetails(id);
      })
    );
  }

  onGoBack(): void {
    this.location.back();
  }
}
<div class="row" *ngIf="(character | async) as character">
    <div class="col-xs-12 col-sm-6 col-md-4">
        <div class="card">
            <div class="image">
                    <img
                    [src]="character.image"
                    [alt]="character.name"
                    class="card-img-top"
                    />  
            </div>
            <div class="card-inner">
                <div class="header">  
                    <h2>{{character.name | slice:0:15}}</h2>
                    <h4 class="text-muted">{{character.gender}}</h4>
                    <small class="text-muted">{{character.created | date}}</small>
                </div>
            </div>
        </div>
    </div>  
</div>
<button
(click)="onGoBack()"
class="btn btn-primary btn-lg mt-3">
    Back
</button>
