import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Observable, take } from 'rxjs';
import { Character } from 'src/app/shared/components/interfaces/character.interface';
import { CharacterService } from 'src/app/shared/services/character.service';
import {Location} from '@angular/common';
@Component({
  selector: 'app-character-details',
  templateUrl: './character-details.component.html',
  styleUrls: ['./character-details.component.css']
})
export class CharacterDetailsComponent {

  character$: Observable<Character> | undefined;

  constructor(private route:ActivatedRoute, private characterSvc: CharacterService,
    private location: Location){}

  ngOnInit(): void{
    this.route.params.pipe(take(1)).subscribe((params) => {
      const id = params['id'];
      this.character$ = this.characterSvc.getDetails(id);
    });
  }

  onGoBack():void{
    this.location.back();
  }
}
<div class="row justify-content-center mt-3" *ngIf="character$ | async as character">
    <div class="col-xs-12 col-sm-6 col-md-6 d-flex justify-content-center mt-5">
       <app-character [character]= "character"></app-character>
    </div>  
</div>
<div class="conainer d-flex justify-content-center">
    <button
    (click)="onGoBack()"
    class="btn btn-dark btn-lg mt-5" style="width: 20%;">
        Back
    </button>
</div>
