import { Component } from '@angular/core';
import { Character } from 'src/app/shared/components/interfaces/character.interface';
import { CharacterService } from 'src/app/shared/services/character.service';
type RequestInfo = {
  next: string;
}

@Component({
  selector: 'app-character-list',
  templateUrl: './character-list.component.html',
  styleUrls: ['./character-list.component.css']
})
export class CharacterListComponent {

  characters: Character[] = [];
  info: RequestInfo = {
    next: null;
  }

  private pageNum=1;
  private query:string | undefined;
  private hideScrollHeight=200;
  private showScrollHeight = 500;

  constructor(private characterSvc: CharacterService){}

  private getDataFromService():void{
    this.characterSvc.searchCharacters(this.query, this.pageNum)
    .pipe(
      take(1)
    ).subscribe((res:any) => {
      const {info, results} = res;
      this.characters = [...this.characters, ...results]
    })
  }
}
<div class="row">
    <div class="col-xs-12 col-sm-6 col-md-4" *ngFor="let character of characters">

        <div class="card">
            <div class="image">
                <a [routerLink]="['/character-details', character.id]">Link
                    <img
                    [src]="character.image"
                    [alt]="character.name"
                    class="card-img-top"
                    />
                </a>
            </div>
            <div class="card-inner">
                <div class="header">
                    <a [routerLink]="['/character-details', character.id]">
                    <h2>character.name</h2>
                    </a>
                    <h4 class="text-muted">character.gender</h4>
                    <small class="text-muted">character.created</small>
                </div>
            </div>
        </div>






    </div>
    <div class="col-xs-12 col-sm-12 col-md-12 mt-5 container-notFound">
        <p>No results......</p>
    </div>
</div>
