import{ Component, Input, ChangeDetectionStrategy} from '@angular/core';
import { Character } from 'src/app/shared/components/interfaces/character.interface';
import { Episode } from 'src/app/shared/components/interfaces/episode.interface';
import { CharacterService } from 'src/app/shared/services/character.service';

@Component({
    selector: 'app-character',
    template: `
    <div class="card bg-dark text-white border border-2 border-black">
            <div class="image">
                <a [routerLink]="['/character-details', character.id]">
                    <img
                    [src]="character.image"
                    [alt]="character.name"
                    class="card-img-top"
                    />
                </a>
            </div>
            <div class="card-inner  text-center">
  <div class="name-container">
    <a [routerLink]="['/character-details', character.id]" class="text-decoration-none text-white">
      <h2 class="pa">{{ character.name | slice: 0:15 }}</h2>
    </a>
    <span *ngIf="alive" class="status-span alive">ðŸŸ¢{{ character.status }} - {{ character.species }}</span>
    <span *ngIf="dead" class="status-span dead">ðŸ”´{{ character.status }} - {{ character.species }}</span>
    <span *ngIf="unknown" class="status-span unknown">ðŸŸ£{{ character.status }} - {{ character.species }}</span>
  </div>
  <div class="created-container">
    <small>Creation: {{ character.created | date }}</small>
  </div>
  <div class="gender-container">
    <p class="ph5">Location: {{ character.origin.name}}</p>
  </div>
  
</div>

        </div>
    `,
    changeDetection:ChangeDetectionStrategy.OnPush
})

export class CharacterComponent{
    @Input()character!:Character;
    episode: Episode | undefined;
    get alive(): boolean {
        return this.character.status === 'Alive';
    }

    get dead(): boolean {
        return this.character.status === 'Dead';
    }

    get unknown(): boolean {
        return this.character.status === 'unknown';
    }

  constructor(private episodeService: CharacterService) {}

  ngOnInit() {
    this.getEpisode();
  }

  getEpisode() {
    this.episodeService.getEpisodeByCharacter(this.character.id)
      .subscribe(
        (episode: Episode) => {
          this.episode = episode;
        },
        error => {
          console.error('Error al obtener el episodio:', error);
        }
      );
  }
}
